import React, { useState, useEffect } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:5000"); // Conectamos con el backend

const VideoCall = () => {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);

  // Escuchar mensajes entrantes
  useEffect(() => {
    socket.on("receiveMessage", (newMessage) => {
      setMessages((prevMessages) => [...prevMessages, newMessage]);
    });

    return () => {
      socket.off("receiveMessage");
    };
  }, []);

  // Enviar mensaje al servidor
  const sendMessage = () => {
    if (message.trim() !== "") {
      socket.emit("sendMessage", message);
      setMessage("");
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen p-4 bg-gray-100">
      <h2 className="text-2xl font-bold mb-4">Videollamada</h2>

      {/* Chat */}
      <div className="w-96 bg-white p-4 rounded shadow-lg">
        <div className="h-64 overflow-y-auto border p-2 mb-2">
          {messages.map((msg, index) => (
            <div key={index} className="bg-blue-200 p-2 rounded mb-1">
              {msg}
            </div>
          ))}
        </div>

        {/* Input para enviar mensaje */}
        <div className="flex">
          <input
            type="text"
            className="flex-1 p-2 border rounded"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Escribe un mensaje..."
          />
          <button
            className="bg-blue-500 text-white px-4 py-2 ml-2 rounded"
            onClick={sendMessage}
          >
            Enviar
          </button>
        </div>
      </div>
    </div>
  );
};

export default VideoCall;
