import React, { useEffect, useRef, useState } from "react";

const VideoCall = () => {
  const localVideoRef = useRef(null);
  const [error, setError] = useState(null); // Estado para manejar errores

  useEffect(() => {
    // Verificamos si el navegador soporta getUserMedia
    if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
      setError("Este navegador no soporta acceso a la cámara y micrófono.");
      return;
    }

    // Solicitar acceso a la cámara y el micrófono
    navigator.mediaDevices.getUserMedia({ video: true, audio: true })
      .then((stream) => {
        if (localVideoRef.current) {
          localVideoRef.current.srcObject = stream; // Mostrar el video local
        }
      })
      .catch((err) => {
        console.error("Error al acceder a la cámara y micrófono:", err);
        setError("No se pudo acceder a la cámara y micrófono. Revisa los permisos.");
      });
  }, []);

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-900 text-white">
      <h2 className="text-2xl mb-4">Videollamada</h2>
      
      {/* Si hay error, mostrar mensaje */}
      {error ? (
        <p className="text-red-500">{error}</p>
      ) : (
        <video ref={localVideoRef} autoPlay playsInline className="w-1/2 border-2 border-white rounded-md shadow-lg" />
      )}
    </div>
  );
};

export default VideoCall;
